-- [ Helpers ] --
-----------------

-- from https://stackoverflow.com/a/9080080/4323865
local function toBits(num, bits)
  -- returns a table of bits, most significant first.
  bits = bits or math.max(1, select(2, math.frexp(num)))

  local t = {} -- will contain the bits

  for b = bits, 1, -1 do
    t[b] = math.fmod(num, 2)
    num = math.floor((num - t[b]) / 2)
  end

  return t
end

local function letterToBits(letterBits, fontMeta)
  local bits = {}

  if letterBits == nil then
    return bits
  end

  local wordBits = fontMeta.words * 8
  local lineBits = fontMeta.lines * fontMeta.width
  local startBit = wordBits - lineBits
  local bitCount = 1

  for i = 1, #letterBits do
    local tBits = toBits(letterBits[i], 8) -- assumes 8bit words

    for j = 1, #tBits do
      if bitCount > startBit then
        bits[#bits + 1] = tBits[j]
      end

      bitCount = bitCount + 1

      if bitCount > wordBits then
        bitCount = 1
      end
    end
  end

  return bits
end

local function drawChar(surface, character, x, y, font, textColor)
  local bits = font[character]

  if bits ~= nil then
    local w, h = font.width, font.height
    local row, col = 1, 1

    for i = 1, #bits do
      local bit = bits[i]

      if bit == 1 then
        surface:drawPixel(x + col - 1, y + row - 1, textColor, textColor, " ")
      end

      col = col + 1

      if col > w then
        col = 1
        row = row + 1
      end

      if row > h then
        break
      end
    end
  end
end


