-- [ Font ] --
--------------

local Font = Class("Font")

function Font:initialize(data, meta)
  self.width = meta.width
  self.height = meta.height

  for k, v in pairs(data) do
    self[k] = letterToBits(v, meta)
  end
end

function Font:getSize()
  return self.width, self.height
end


