local Fonts = Class("Fonts")

function Fonts:initialize()
  self.fonts = {}

  table.insert(self.fonts, Font:new(font4x6, meta4x6))
  table.insert(self.fonts, Font:new(font5x12, meta5x12))
  table.insert(self.fonts, Font:new(font6x8, meta6x8))
  table.insert(self.fonts, Font:new(font7x12, meta7x12))
  table.insert(self.fonts, Font:new(font8x12, meta8x12))
  table.insert(self.fonts, Font:new(font12x16, meta12x16))
end

function Fonts:drawString(surface, text, x, y, fontSize, textColor)
  font = self.fonts[fontSize or 1]
  textColor = textColor or colors.white
  x = x or 0
  y = y or 0

  for letter in text:gmatch(".") do
    drawChar(surface, letter, x, y, font, textColor)
    x = x + font.width
  end
end

function Fonts:getStringSize(text, fontSize)
  local font = self.fonts[fontSize or 1]
  local w, h = font:getSize()

  return #text * w, h
end

return Fonts
